import '../App.css';
import React, { useContext } from 'react'
import {Card, Button} from 'react-bootstrap'
import { ShopContext } from '../context/shopContext'
import { Link } from 'react-router-dom'
import AddToCartButton from '../components/AddToCartButton.js'



const Item = ({product, size}) => {
  function nameLength(){
    if(product.title.length>20){
      var productTitle=product.title.substring(0,16)+"...";
      return(productTitle);
    }if(product.title.length>!20){
    return(product.title);
  }
  }
const {addCommas} = useContext(ShopContext)
  return(
    <div className="items">
    <Card style = {{ width: size}} >
      <Card.Img   className="items-image" src={product.images[0].src}/>
        <div className="items-title">
        <h1>{nameLength()}</h1>
        </div>
        <div>
        <h2>${addCommas(product.variants[0].price)}</h2>
        </div>
        <p>{product.description}</p>
         <Link to={`/product/${product.id}`}>
          <Button className="items-button" block >More Details</Button>
          </Link>
          <AddToCartButton  product={product} />
    </Card>
    <br></br>
    </div>
  )


}



export default Item
